#include <iostream>

using namespace std;

int main()
{
    int a = 2;
    int b = 2;

    bool hasil1, hasil2;

    //komparasi

    //sebanding
    cout<< (a == b) << endl;

    //tidak sebanding
    cout<< (a != b) << endl;

    //kurang dari
    cout<< (a < b) << endl;

    //lebih dari
    cout<< (a > b) << endl;

    //kurang dari sama dengan
    cout<< (a <= b) << endl;

    //lebih dari sama dengan
    cout<< (a >= b) << endl;

    
}